import json
parsed=json.loads(result,strict=False)
reviewIds=[]
chunkNumbers=[]
similarity_scores=[]

for item in parsed:
    reviewIds.append(item['reviewId'])
    chunkNumbers.append(item['chunkNumber'])
    similarity_scores.append(item['similarity_score'])

import matplotlib.pyplot as plt
import mplcursors

fig, ax = plt.subplots()
counts, bins, bars = ax.hist(similarity_scores, bins = 30, alpha=0.5, color='b', edgecolor='black', linewidth=1.2, histtype='bar', rwidth=0.8)
sc1 = ax.scatter(similarity_scores, reviewIds, color='r', label='reviewId')
sc2 = ax.scatter(similarity_scores, chunkNumbers, color='g', label='chunkNumber')
plt.legend()
plt.title('Histogram of similarity scores')
plt.xlabel('similarity scores')
plt.ylabel('count')

cursor = mplcursors.cursor([sc1, sc2], hover=True)

@cursor.connect("add")
def on_add(sel):
    sel.annotation.set_text(f"Review ID: {reviewIds[sel.target.index]}, Chunk Number: {chunkNumbers[sel.target.index]}")

plt.show()

